import styled from "styled-components";
import usuario from "../../assets/usuario.png";

const UserStyled = styled.div`
  button {
    cursor: pointer;
  }

  section {
    background-color: #f7f8fc;
    height: auto;
    width: 100vw;
    display: flex;
    justify-content: center;
    align-items: center;
  }

  .div-form {
    background-color: #fff;
    border-radius: 5px;
    border: #c5c7cd solid 1px;
    height: 85vh;
    width: 97vw;
    margin-top: 8px;
    margin-left: -10px;
    margin-bottom: 8px;
    display: flex;
    justify-content: center;
    overflow-y: auto;
  }

  .avatar {
    background-image: url(${usuario});
    width: 20vw;
    height: 20vw;
    background-size: cover;
    background-position: center center;
    border-radius: 50%;
    display: flex;
    justify-content: center;
    align-items: center;
  }
`;

export default UserStyled;
