import { createGlobalStyle } from "styled-components";

const GlobalStyle = createGlobalStyle`
    * {
        font-family: "Mulish", sans-serif;
        margin: 0;
        padding: 0;
        box-sizing: border-box;   
    }

    body {
        overflow: hidden;
    }

    textarea {
    height: 200px;
    width: 96%;
    margin-left: 2%;
    margin-top: 18px;
    background-color: #d9d9d9;
    border: none;
    border-radius: 8px;
    margin-bottom: 17px;
    text-align: start;
    outline: none;
    padding-left: 0.7rem;
    resize: none;
    padding-top: 0.5rem;
  }
`;

export default GlobalStyle;
