import React from "react";
import FormStyled from "./FormStyled";
import { useState, useEffect } from "react";
import axios from "axios";
import {
  useAutocomplete,
  AutocompleteGetTagProps,
} from "@mui/base/AutocompleteUnstyled";
import CheckIcon from "@mui/icons-material/Check";
import CloseIcon from "@mui/icons-material/Close";
import { styled } from "@mui/material/styles";
import { autocompleteClasses } from "@mui/material/Autocomplete";

const Root = styled("div")(
  ({ theme }) => `
  color: ${
    theme.palette.mode === "dark" ? "rgba(255,255,255,0.65)" : "rgba(0,0,0,.85)"
  };
  font-size: 14px;
  position: relative;
`
);

const Label = styled("label")`
  padding: 0 0 4px;
  line-height: 1.5;
  display: block;
`;

const InputWrapper = styled("div")(
  ({ theme }) => `
  width: 96%;
  border: none;
  background-color: ${theme.palette.mode === "dark" ? "#141414" : "#fff"};
  border-radius: 4px;
  padding: 1px;
  display: flex;
  flex-wrap: wrap;

  & input {
    background-color: ${theme.palette.mode === "dark" ? "#141414" : "#fff"};
    color: ${
      theme.palette.mode === "dark"
        ? "rgba(255,255,255,0.65)"
        : "rgba(0,0,0,.85)"
    };
    height: 30px;
    box-sizing: border-box;
    padding: 4px 6px;
    width: 0;
    min-width: 30px;
    flex-grow: 1;
    border: 0;
    margin: 0;
    outline: 0;
  }
`
);

interface TagProps extends ReturnType<AutocompleteGetTagProps> {
  label: string;
}

function Tag(props: TagProps) {
  const { label, onDelete, ...other } = props;
  return (
    <div {...other}>
      <span>{label}</span>
      <CloseIcon onClick={onDelete} />
    </div>
  );
}

const StyledTag = styled(Tag)<TagProps>(
  ({ theme }) => `
  display: flex;
  align-items: center;
  height: 24px;
  margin: 2px;
  line-height: 22px;
  background-color: ${
    theme.palette.mode === "dark" ? "rgba(255,255,255,0.08)" : "#fafafa"
  };
  border: 1px solid ${theme.palette.mode === "dark" ? "#303030" : "#e8e8e8"};
  border-radius: 7px;
  box-sizing: content-box;
  padding: 0 10px 0 10px;
  outline: 0;
  overflow: hidden;

  &:focus {
    border-color: ${theme.palette.mode === "dark" ? "#177ddc" : "#40a9ff"};
    background-color: ${theme.palette.mode === "dark" ? "#003b57" : "#e6f7ff"};
  }

  & span {
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
  }

  & svg {
    font-size: 12px;
    cursor: pointer;
    margin-left: 7px;
  }
`
);

const Listbox = styled("ul")(
  ({ theme }) => `
  width: 300px;
  margin-left: 15px;
  margin-top: -45px;
  padding: 0;
  position: absolute;
  list-style: none;
  background-color: ${theme.palette.mode === "dark" ? "#141414" : "#fff"};
  overflow: auto;
  max-height: 250px;
  border-radius: 4px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.15);
  z-index: 1;

  & li {
    padding: 5px 12px;
    display: flex;

    & span {
      flex-grow: 1;
    }

    & svg {
      color: transparent;
    }
  }

  & li[aria-selected='true'] {
    background-color: ${theme.palette.mode === "dark" ? "#2b2b2b" : "#fafafa"};
    font-weight: 600;

    & svg {
      color: #1890ff;
    }
  }

  & li.${autocompleteClasses.focused} {
    background-color: ${theme.palette.mode === "dark" ? "#003b57" : "#e6f7ff"};
    cursor: pointer;

    & svg {
      color: currentColor;
    }
  }

  &::-webkit-scrollbar {
    width: 0.6rem;
  }

  &::-webkit-scrollbar-track {
    border-radius: 1rem;
    margin-top: 0.4rem;
    margin-bottom: 0.4rem;
  }

  &::-webkit-scrollbar-thumb {
    background: rgba(33, 104, 0, 0.4);
    border-radius: 1rem;
  }

  &::-webkit-scrollbar-thumb:hover {
    background: rgba(33, 104, 0, 0.7);
  }
`
);

const Form: React.FC = () => {
  const timeElapsed = Date.now();
  const today = new Date(timeElapsed);
  const hoje = today.toLocaleDateString();

  const [showElement3, setShowElement3] = useState(false);
  const showOrHide3 = () => setShowElement3(!showElement3);

  const [nome, setNome] = useState<string>();
  const [sobrenome, setSobrenome] = useState<string>();
  const [id, setId] = useState<string>();
  const [telefone, setTelefone] = useState<string>();
  const [setor, setSetor] = useState<string>();
  const [email, setEmail] = useState<string>();
  const [foto, setFoto] = useState<string>();

  const [idCRM, setIdCRM] = useState();
  const [idCRMDisplay, setIdCRMDisplay] = useState();
  const [versao, setVersao] = useState(1);
  const [descricao, setDescricao] = useState<string>();
  const [necessidade, setNecessidade] = useState<string>();
  const [impacto, setImpacto] = useState<string>();
  const [objetivo, setObjetivo] = useState<string>();
  const [justificativa, setJustificativa] = useState<string>();
  const [alternativa, setAlternativa] = useState<string>();
  const [data_obrigatoriedade, setData_obrigatoriedade] = useState<string>();
  const [complexidade, setComplexidade] = useState(0);
  const [sistemas_envolvidos, setSistemas_envolvidos] = useState<string[]>([]);
  const [comportamento_offline, setComportamento_offline] = useState<string>();
  const [dependencia_outro_crm, setDependendia_outro_crm] =
    useState<string>("nao");
  const [numero_crm_dependencia, setNumero_dependencia_crm] =
    useState<string>();
  const [documento_anexo, setDocumento_anexo] = useState<string>();
  const [nomedocumento, setNomedocumento] = useState<string>();

  interface FilmOptionType {
    setor: string;
  }

  const [listaSetores, setListaSetores] = useState<FilmOptionType[]>([
    { setor: "Administrativo" },
    { setor: "Comercial" },
    { setor: "Compras" },
    { setor: "Contabilidade" },
    { setor: "Financeiro" },
    { setor: "Marketing" },
    { setor: "Mercantil" },
    { setor: "Operações" },
    { setor: "RH" },
    { setor: "TI" },
  ]);

  useEffect(() => {
    let usuario = localStorage.getItem("usuario-logado");
    axios
      .post("http://localhost:3001/getUser", { usuario: usuario })
      .then((response) => {
        setNome(response.data.nome);
        setSobrenome(response.data.sobrenome);
        setId(response.data.matricula);
        setTelefone(response.data.telefone);
        setSetor(response.data.setor);
        setEmail(response.data.email);
        setFoto(response.data.foto_perfil);
      });
    axios.get("http://localhost:3001/getMaxCRM").then((response) => {
      setIdCRM(response.data[0][0].valor + 1);
      const n = (response.data[0][0].valor + 1).toLocaleString("en-US", {
        minimumIntegerDigits: 5,
        useGrouping: false,
      });
      setIdCRMDisplay(n);
    });
  }, []);

  function criarCRM(e: any) {
    e.preventDefault();
    let listaSetoresCheck: any = [];
    valor.map((value) => {
      listaSetoresCheck.push(value.setor);
    });
    axios
      .post("http://localhost:3001/createCRM", {
        id: idCRM,
        versao_crm: versao,
        colaborador_setor: setor,
        colaborador_id: id,
        descricao: descricao,
        necessidade: necessidade,
        impacto: impacto,
        objetivo: objetivo,
        justificativa: justificativa,
        alternativas: alternativa,
        data_obrigatoriedade: data_obrigatoriedade,
        complexidade: complexidade,
        setores_envolvidos: JSON.stringify(listaSetoresCheck),
        sistemas_envolvidos: JSON.stringify(sistemas_envolvidos),
        comportamento_offline: comportamento_offline,
        dependencia_outro_crm: dependencia_outro_crm,
        numero_crm_dependencia: numero_crm_dependencia,
        documento_anexo: documento_anexo,
        data_inicio: today,
      })
      .then((response) => {
        alert(response.data);
      });
  }

  const fixedOptions = [listaSetores[9]];
  const [valor, setValor] = React.useState([...fixedOptions]);

  const {
    getRootProps,
    getInputLabelProps,
    getInputProps,
    getTagProps,
    getListboxProps,
    getOptionProps,
    groupedOptions,
    value,
    focused,
    setAnchorEl,
  } = useAutocomplete({
    id: "customized-hook-demo",
    defaultValue: [listaSetores[9]],
    multiple: true,
    options: listaSetores,
    value: valor,
    onChange: (event, newValue) => {
      setValor([
        ...fixedOptions,
        ...newValue.filter((option) => fixedOptions.indexOf(option) === -1),
      ]);
    },
    getOptionLabel: (option) => option.setor,
  });

  function convertFile(files: FileList | null, values: string | null) {
    if (files) {
      const fileRef = files[0] || "";
      const fileType: string = fileRef.type || "";
      const reader = new FileReader();
      reader.readAsBinaryString(fileRef);
      reader.onload = (ev: any) => {
        // convert it to base64
        setDocumento_anexo(`data:${fileType};base64,${btoa(ev.target.result)}`);
      };
    }
    if (values) {
      var filename = values.replace(/^.*\\/, "");
      setNomedocumento(filename);
    }
  }

  console.log(nomedocumento);

  return (
    <FormStyled foto_perfil={foto}>
      <section>
        <div className="div-form mt-4">
          <div className="posicao-form">
            <form action="">
              <h1>Número de cadastro da CRM:{idCRMDisplay}</h1>
              <h3>Responsável:</h3>
              <div className="div-usuario">
                <div className="foto-usuario"></div>
                <p className="texto-usuario">
                  Nome: {nome! + " " + sobrenome!}
                  <br />
                  Matrícula: {id}
                  <br />
                  Setor: {setor}
                  <br />
                  Telefone: {telefone}
                  <br />
                  E-mail: {email}
                </p>
              </div>
              <Root>
                <div {...getRootProps()}>
                  <Label {...getInputLabelProps()}>Setores Envolvidos:</Label>
                  <InputWrapper
                    ref={setAnchorEl}
                    className={focused ? "focused" : ""}
                  >
                    <input {...getInputProps()} />
                    {value.map((option: FilmOptionType, index: number) => (
                      <StyledTag
                        label={option.setor}
                        {...getTagProps({ index })}
                      />
                    ))}
                  </InputWrapper>
                </div>
                {groupedOptions.length > 0 ? (
                  <Listbox {...getListboxProps()}>
                    {(groupedOptions as typeof listaSetores).map(
                      (option, index) => (
                        <li {...getOptionProps({ option, index })}>
                          <span>{option.setor}</span>
                          <CheckIcon fontSize="small" />
                        </li>
                      )
                    )}
                  </Listbox>
                ) : null}
              </Root>
              <label htmlFor="necessidade" className="pt-3">
                Necessidade:
              </label>
              <textarea
                id=""
                className="input-objetivo"
                value={necessidade}
                onChange={(e) => setNecessidade(e.target.value)}
              />
              <label htmlFor="impacto" className="pt-3">
                Impacto:
              </label>
              <textarea
                name="impacto"
                id=""
                className="input-objetivo"
                value={impacto}
                onChange={(e) => setImpacto(e.target.value)}
              />
              <label htmlFor="descricao" className="pt-3">
                Descrição:
              </label>
              <textarea
                name="descricao"
                id=""
                className="input-descricao"
                value={descricao}
                onChange={(e) => setDescricao(e.target.value)}
              />
              <label htmlFor="objetivo">Objetivo:</label>
              <textarea
                name="objetivo"
                id=""
                className="input-descricao"
                value={objetivo}
                onChange={(e) => setObjetivo(e.target.value)}
              />
              <label htmlFor="justificativa">Justificativa:</label>
              <textarea
                name="justificativa"
                id=""
                className="input-descricao"
                value={justificativa}
                onChange={(e) => setJustificativa(e.target.value)}
              />
              <div className="div-versao-data">
                <div className="div-usuario">
                  <p>Versão:</p>
                  <p className="ps-2">v{versao}</p>
                </div>
                <div className="div-usuario">
                  <p>Data criação:</p>
                  <p className="ps-2">{hoje}</p>
                </div>
              </div>
              <label htmlFor="data-legal">
                Data Legal &#40;caso exista&#41;:
              </label>
              <input
                type="date"
                name="data-legal"
                id=""
                className="input-data-legal"
                value={data_obrigatoriedade}
                onChange={(e) => setData_obrigatoriedade(e.target.value)}
              />
              <br />
              <h3 className="mt-3">Sistemas envolvidos na mudança:</h3>
              <div className="d-flex justify-content-between">
                <div>
                  <label htmlFor="CLIQQ" className="label-checkbox">
                    CLIQQ:
                  </label>
                  <input
                    type="checkbox"
                    name="CLIQQ"
                    value="CLIQQ"
                    id=""
                    className="checkbox ms-1"
                    onChange={(e) =>
                      setSistemas_envolvidos(
                        sistemas_envolvidos?.concat(e.target.value)
                      )
                    }
                  />
                </div>
                <div>
                  <label htmlFor="PDV" className="label-checkbox">
                    PDV:
                  </label>
                  <input
                    type="checkbox"
                    name="PDV"
                    value="PDV"
                    id=""
                    className="checkbox ms-1"
                    onChange={(e) =>
                      setSistemas_envolvidos(
                        sistemas_envolvidos?.concat(e.target.value)
                      )
                    }
                  />
                </div>
                <div>
                  <label htmlFor="VERDECARD" className="label-checkbox">
                    VERDECARD:
                  </label>
                  <input
                    type="checkbox"
                    name="VERDECARD"
                    value="VERDECARD"
                    id=""
                    className="checkbox ms-1"
                    onChange={(e) =>
                      setSistemas_envolvidos(
                        sistemas_envolvidos?.concat(e.target.value)
                      )
                    }
                  />
                </div>
                <div>
                  <label htmlFor="SAP" className="label-checkbox">
                    SAP:
                  </label>
                  <input
                    type="checkbox"
                    name="SAP"
                    value="SAP"
                    id=""
                    className="checkbox ms-1"
                    onChange={(e) =>
                      setSistemas_envolvidos(
                        sistemas_envolvidos?.concat(e.target.value)
                      )
                    }
                  />
                </div>
                <div className="me-5">
                  <label htmlFor="VCS" className="label-checkbox">
                    VCS:
                  </label>
                  <input
                    type="checkbox"
                    name="VCS"
                    value="VCS"
                    id=""
                    className="checkbox ms-1"
                    onChange={(e) =>
                      setSistemas_envolvidos(
                        sistemas_envolvidos?.concat(e.target.value)
                      )
                    }
                  />
                </div>
              </div>
              <label className="mt-3" htmlFor="comportamento-offline">
                Comportamento Offline &#40;time Mercantil&#41;:
              </label>
              <textarea
                name="comportamento-offline"
                id=""
                className="input-justificativa"
                value={comportamento_offline}
                onChange={(e) => setComportamento_offline(e.target.value)}
              />
              <h3 className="mt-3">
                Essa CRM depende de outro desenvolvimento?
              </h3>
              <div className="d-flex justify-content-start">
                <label className="label-checkbox">
                  Sim:
                  <input
                    type="radio"
                    name="depende"
                    value="sim"
                    id=""
                    className="checkbox ms-1"
                    onChange={(e) => setDependendia_outro_crm(e.target.value)}
                    onClick={showOrHide3}
                  />
                </label>

                <label className="label-checkbox ms-2">
                  Não:
                  <input
                    type="radio"
                    name="depende"
                    value="nao"
                    id=""
                    className="checkbox ms-1"
                    onChange={(e) => setDependendia_outro_crm(e.target.value)}
                    onClick={showOrHide3}
                  />
                </label>
              </div>
              {showElement3 ? (
                <div>
                  <label htmlFor="numero-dependencia">
                    Número CRM necessário:
                  </label>
                  <input
                    type="text"
                    name="numero-dependencia"
                    id=""
                    className="input-data-legal"
                    value={numero_crm_dependencia}
                    onChange={(e) => setNumero_dependencia_crm(e.target.value)}
                  />
                </div>
              ) : null}
              <label className="mt-3" htmlFor="documentos">
                Documentos:
              </label>
              <div className="input-documentos">
                {documento_anexo && (
                  <>
                    {documento_anexo.indexOf("image/") > -1 && (
                      <div className="arquivo ms-5 mt-5">
                        <i className="far fa-file-image download"></i>
                        <p className="texto-usuario">{nomedocumento}</p>
                      </div>
                    )}
                  </>
                )}
              </div>
              <br />
              <label htmlFor="" className="anexar">
                <i className="fa-solid fa-upload"></i>
                Anexar documentos
                <input
                  type="file"
                  name=""
                  id=""
                  onChange={(e) => convertFile(e.target.files, e.target.value)}
                />
              </label>
              <div className="div-salvar">
                <button
                  type="submit"
                  className="botao-salvar"
                  onClick={criarCRM}
                >
                  SALVAR
                </button>
              </div>
            </form>
          </div>
        </div>
      </section>
    </FormStyled>
  );
};

export default Form;
