                  <div className="texto-usuario d-flex justify-content-end">
                    12 de Outubro de 2022
                  </div> */}
                  <h6 className="pt-3">Complexidade:</h6>
                  <div className="texto-usuario d-flex justify-content-end">
                    {switchComplexidade(complexidade)}
                  </div>
                </div>
              </div>
            </section>
          </div>
        </div>
      </div>
    </AccordionStyled>
  );
};

export default Accordion;
